        );
    }

    function handleClick(lineId){
        return(
            setList(list.map(
                p => p.key === lineId ? {...p, clicked:true} : {...p, clicked:false}))
        );
    }

    function checkCount() {
        // console.log(list.filter((p) => p.checked === true));
        // console.log(count)
        return(count>0);
    }

  return (
