import "./Line.css";
import "./Lab1EmptyList.css";

function Line(props) {
    let clicked = "false"
    function emptyCheck(props) {
        return (
            props.field !== ""
        );
    }
    function clearer() {
        if (clicked === "true") {
            return " ";
        }
        else {
            return (props.line.field);
        }
    }

    function lineLogic(props) {
        if (emptyCheck(props)) {
            return (<li>
                        <input className={"checkboxes"}
                               type={"checkbox"}>
                        </input>
                        <input className={"textboxes"}
                               type={"text"}
                               onClick={(e) => {
                                                            e.stopPropagation();
                                                            if (props.isTapLine) {clicked="true"};
                                                            }}
                               onChange={
                                   (e) => props.onLineChangeField(props.line.key, e.target.value)
                               }
                               value={clearer()}>
                        </input>
                    </li>);
        }
        else {
            return (<li>
                        <input className={"emptyCheckbox"}
                               type={"checkbox"}>
                        </input>
                        <input className={"textboxes"}
                               disabled={true}
                               type={"text"}
                               value={props.field}>
                        </input>
                    </li>);
        }
    }
return (
    <div className={"all-lines"}>
        {lineLogic(props)}
        {console.log(clearer)}
    </div>
)
}

export default Line;
